=cut
__DATA__
___[ xt/release/kwalitee.t ]___
#!perl

# This test is generated by Dist::Zilla::Plugin::Test::Kwalitee::Extra
use strict;
use warnings;
use Test::More;   # needed to provide plan.
{{ $tests }}

plan skip_all => "Test::Kwalitee::Extra required for testing kwalitee: $@" if $@;
